mapper.select:
SELECT `book`.`id` AS `book_id`, `book`.`name` AS `book_name`, `course`.`id` AS `course_id`, `course`.`name` AS `course_name`, `user`.`id` AS `user_id`, `user`.`nickname` AS `user_nickname`, `userCourseRepeat`.`id` AS `userCourseRepeat_id`, `userCourseRepeat`.`create_time` AS `userCourseRepeat_create_time` FROM `t_user_course_repeat` AS `userCourseRepeat` LEFT JOIN `t_book` AS `book` ON `book`.`id` = `userCourseRepeat`.`book_id` LEFT JOIN `t_user_course` AS `userCourse` ON `userCourse`.`id` = `userCourseRepeat`.`user_course_id` LEFT JOIN `t_user` AS `user` ON `user`.`id` = `userCourse`.`user_id` LEFT JOIN `t_course` AS `course` ON `course`.`id` = `userCourse`.`course_id` WHERE `userCourseRepeat`.`del_flag` = FALSE
AND DATE_FORMAT(`userCourseRepeat`.`update_time`,'%Y-%m-%d') >= #{beginDate}
AND DATE_FORMAT(`userCourseRepeat`.`update_time`,'%Y-%m-%d') <= #{endDate}
ORDER BY `userCourseRepeat`.`update_time` ASC
